import './styles.css';
import { useState } from 'react';

const Form = ({listTransactions, setListTransactions}) => {

  const [inputDescription, setInputDescription] = useState('');
  const [value, setValue]             = useState('');
  const [type, setType]               = useState('Entrada');
  const [id, setId]                   = useState(1);

  const addNewTransaction = (description, value, type) => {

    if (description !== '' && value !== '' ) {
      const newTransaction = {};

      newTransaction.inputDescription = description;
      newTransaction.value       = value;
      newTransaction.type        = type;
      newTransaction.id          = id;

      console.log(newTransaction);
      setListTransactions([...listTransactions, newTransaction]);
      setInputDescription('');
      setValue('');
      setId(id + 1);
    }

  }


  return (
    <div>
      <form onSubmit={(e) => e.preventDefault()}>

        <p className='description'>Descrição</p>
        <input 
        className='input__description'
        type="text" 
        placeholder='Digite aqui sua descrição'
        />
        <p className='example'>Ex: Compra de roupas</p>

        <span className='type__values'>
        <p>Valor</p>
        <p>Tipo de valor</p>
        </span>

        <span className='values__select'>
          <input className='value__money' type="text" placeholder='R$ 1,00'/>
          <select className='value__select' name="select" placeholder='Tipo de Valor'>
            <option className='select__in' value="entrada">Entrada</option>
            <option className='select__out' value="saida">Saída</option>
          </select>
        </span>

        <button onClick={() => addNewTransaction(inputDescription, value, type)}>Inserir Valor</button>

      </form>
    </div>
  )
}

export default Form;
